sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	if (g_form.getValue('file_type') == 'pdf') {
		return;
	}

	var message = "Use the following variables in Headers:**${date}: inserts current date**${writtenDate}: inserts current date in format 'dd MMMMM yyyy' **${usersName}: inserts name if send from related UI Action**${pageNumber}: inserts the current page's number**${pageTotal}: inserts the total number of pages";

	var messagArray = message.split('**');

	for (var i=0; messagArray.length>i; i++)	{

		g_form.showFieldMsg('header_first_page', messagArray[i], 'info');
	}

}
